export const PREDICT_TO_EXTENSION = 'predict-to-extension';
export const EXTENSION_TO_PREDICT = 'extension-to-predict';

type PredictToolsMessageType =
  | 'request-init'
  | 'request-highlight-zone'
  | 'request-unhighlight-zone'
  | 'request-scroll-to-zone'
  | 'disconnect';

export type PredictToolsMessage<TData> = {
  type: PredictToolsMessageType;
  data: TData;
};

type PredictMessageType = 'init' | 'state-change';

export type PredictMessage<TData> = {
  type: PredictMessageType;
  data: TData;
};
